export class DrawingBox {
    constructor() {
        this.x = 0;
        this.y = 0;
        this.height = 0;
        this.width = 0;
    }
    getCenterX() {
        return this.x + this.width / 2;
    }
    getCenterY() {
        return this.y + this.height / 2;
    }
    clone() {
        const that = new DrawingBox();
        Object.assign(that, {
            height: this.height,
            width: this.width,
            x: this.x,
            y: this.y,
        });
        return that;
    }
}
