import { ALIGN_LEFT } from '../drawer/BaseDrawer';
import { DrawingBox } from './GraphicBox';
export const TITLE_PADDING = 5;
export const TITLE_MARGIN = 0;
export class TitleGraphic {
    constructor() {
        this.box = new DrawingBox();
    }
    draw(drawer) {
        drawer.drawTextBox(this.box, this.message, TITLE_MARGIN, TITLE_PADDING, ALIGN_LEFT);
    }
}
