export class Signal {
    constructor(actorA, signalType, actorB, message) {
        this.type = 'Signal';
        this.actorA = actorA;
        this.actorB = actorB;
        // tslint:disable-next-line:no-bitwise
        this.lineType = signalType & 3;
        // tslint:disable-next-line:no-bitwise
        this.arrowType = (signalType >> 2) & 3;
        this.message = message;
    }
    isSelf() {
        return this.actorA.index === this.actorB.index;
    }
}
